mapper.delete:
DELETE FROM board
WHERE board_seq = #{boardSeq}
AND
board_type = 'f';

DELETE FROM board
WHERE board_seq = #{boardSeq}
AND
board_type = 's';

mapper.update:
UPDATE board
SET
	title = #{title},
	content = #{content}
WHERE board_seq = #{boardSeq}
AND
user_id = #{userId}
AND
board_type = 'f'

UPDATE board
SET
	title = #{title},
	content = #{content}
WHERE board_seq = #{boardSeq}
AND
user_id = #{userId}
AND
board_type = 's'

mapper.select:
SELECT * FROM board
WHERE board_type = 'm'
AND user_id = #{userId}
AND com_num = #{hospitalComNum}

SELECT * FROM board
WHERE com_num = #{comNum}
AND reservation_date = #{date}


SELECT b.*,u.user_name
FROM board AS b
INNER JOIN user AS u
ON b.com_num = u.com_num
WHERE b.com_num = #{comNum}
and b.reservation_date = #{reservationDate}
and b.schedule_time = #{scheduleTime}
and b.board_type= 'm'

mapper.update:
		UPDATE board
