mapper.select:
SELECT * FROM file
WHERE board_seq = #{boardSeq}
AND board_type = 'f';

SELECT * FROM file
WHERE board_seq = #{boardSeq}
AND board_type = 's';

SELECT * FROM file
WHERE board_seq = #{boardSeq}
AND board_type = 't';

SELECT * FROM file
WHERE board_seq = #{boardSeq}
AND board_type = IFNULL(#{boardType},'f');

SELECT * FROM file
WHERE board_seq = #{boardSeq}
AND board_type = 'm';

mapper.delete:
DELETE FROM file
WHERE board_seq = #{boardSeq}
AND board_type = 't'

DELETE FROM file
WHERE board_seq = #{boardSeq}
AND board_type = 's'

DELETE FROM file
WHERE file_seq      = #{fileSeq}
     AND board_seq = #{boardSeq}
     AND board_type = 'f'

DELETE FROM file
WHERE file_seq      = #{fileSeq}
     AND board_seq = #{boardSeq}
     AND board_type = 's'

DELETE FROM file
WHERE file_seq      = #{fileSeq}
     AND board_seq = #{boardSeq}
     AND board_type = 't'

DELETE FROM file
WHERE file_seq       = #{fileSeq}
     AND board_seq   = #{boardSeq}
     AND board_type = #{boardType}
     AND user_id        = #{userId}

DELETE FROM file
WHERE board_seq = #{boardSeq}
AND board_type = 'f'

DELETE FROM file
WHERE file_seq = #{fileSeq}
AND 
board_seq = #{boardSeq}
AND
board_type = 'm'

DELETE FROM file
WHERE user_id = #{userId}
AND
board_seq = #{userSeq}
AND
board_type = #{boardType}

DELETE FROM file
WHERE
	FILE_SEQ = #{petSeq}
AND
	user_id =#{userId}

DELETE FROM file
WHERE
	BOARD_SEQ= #{vetSeq}
AND
	user_id =#{userId}
AND BOARD_TYPE =#{boardType}

mapper.select:
		select * from file 
			WHERE user_id = #{userId}
    		AND board_type = #{boardType}
    		AND board_seq = #{boardSeq}

mapper.update:
UPDATE FILE
SET
	originalFilename = #{originalFileName},
	filepath = #{filePath},
	filesize = #{fileSize}
WHERE
	board_seq = #{boardSeq}
AND 
	user_id = #{userId}
AND
	board_type = #{boardType}
AND 
	file_seq = #{fileSeq}
