mapper.select:
SELECT * FROM file
WHERE board_seq = #{boardSeq}
AND board_type = 'f';

SELECT * FROM file
WHERE board_seq = #{boardSeq}
AND board_type = 'm';

mapper.delete:
DELETE FROM file
WHERE board_seq = #{boardSeq}
AND board_type = 'f'

DELETE FROM file
WHERE file_seq = #{fileSeq}
AND 
board_seq = #{boardSeq}
AND
board_type = 'f'

DELETE FROM file
WHERE file_seq = #{fileSeq}
AND 
board_seq = #{boardSeq}
AND
board_type = 'm'

DELETE FROM file
WHERE user_id = #{userId}
AND
board_seq = #{userSeq}
AND
board_type = #{boardType}
