mapper.select:
SELECT * FROM file
WHERE board_seq = #{boardSeq}
AND board_type = 'f';

mapper.delete:
DELETE FROM file
WHERE board_seq = #{boardSeq}
AND board_type = 'f'

DELETE FROM file
WHERE file_seq = #{fileSeq}
AND 
board_seq = #{boardSeq}

mapper.update:
UPDATE FILE
SET
	originalFilename = #{originalFileName}
	filepath = #{filePath}
	filesize = #{fileSize}	
WHERE
	board_seq = #{petSeq};
