mapper.select:
SELECT * FROM file
WHERE board_seq = #{boardSeq}
AND board_type = 'f';

SELECT * FROM file
WHERE board_seq = #{boardSeq}
AND board_type = 's';

SELECT * FROM file
WHERE board_seq = #{boardSeq}
AND board_type = 't';

SELECT * FROM file
WHERE board_seq = #{boardSeq}
AND board_type = IFNULL(#{boardType},'f');

mapper.delete:
DELETE FROM file
WHERE board_seq = #{boardSeq}
AND board_type = 'f'

DELETE FROM file
WHERE board_seq = #{boardSeq}
AND board_type = 't'

DELETE FROM file
WHERE board_seq = #{boardSeq}
AND board_type = 's'

DELETE FROM file
WHERE file_seq      = #{fileSeq}
     AND board_seq = #{boardSeq}

DELETE FROM file
WHERE file_seq       = #{fileSeq}
     AND board_seq   = #{boardSeq}
     AND board_type = #{boardType}
     AND user_id        = #{userId}
