mapper.insert:
INSERT INTO PET (

) VALUES (
	 (SELECT IFNULL(MAX(PET_SEQ), 0) + 1 FROM PET A )
	,#{userId}
	,#{name}
	,#{gender}
	,#{age}
	,#{petType}
	,#{petTypeDetail}
	,#{symptom}
	,#{status}

)

mapper.update:
UPDATE PET
SET

	 NAME = #{name}
	, GENDER = #{gender}
	, AGE = #{age}
	, PET_TYPE = #{petType}
	, PET_TYPE_DETAIL = #{petTypeDetail}
	, SYMPTOM = #{symptom}
	, STATUS = #{status}

WHERE
	PET_SEQ = #{petSeq}

mapper.select:
SELECT
FROM PET WHERE STATUS = 'N' AND USER_ID = #{userId}
ORDER BY PET_SEQ DESC
