mapper.insert:
INSERT INTO VET (

) VALUES (
	 (SELECT IFNULL(MAX(VET_SEQ), 0) + 1 FROM VET A )
	,#{hospitalSeq}
	,#{vetName}
	,#{vetMajor}
	,#{vetIntro}

)

mapper.select:
SELECT
FROM VET WHERE HOSPITAL_SEQ = #{hospitalSeq}
ORDER BY VET_SEQ DESC

mapper.update:
UPDATE VET
SET

	 VET_NAME = #{vetName}
	, VET_MAJOR = #{vetMajor}
	, VET_INTRO = #{vetIntro}

WHERE
	VET_SEQ = #{vetSeq}

mapper.select:
SELECT IFNULL(MAX(vet_seq) ,0) + 1
	FROM VET
WHERE HOSPITAL_SEQ = #{hospitalSeq}
